  BIZ_CONFIG_VALUE = #{bizConfigValue,jdbcType=VARCHAR},
  CREATE_TIME = #{createTime,jdbcType=TIMESTAMP},
  UPDATETIME = #{updatetime,jdbcType=TIMESTAMP}
where BIZ_CONFIG_CODE = #{bizConfigCode,jdbcType=VARCHAR}
