mapper.select:
 select 

 from order_baseinfo
 where 1=1

 	and CUST_CELLPHONE=#{custCellphone}


 	and CONTRACT_ID=#{contractId}


 	and ORDER_STATUS=#{orderStatus}


 	and COMPANY_ID=#{companyId}


 	and IS_ASSURE=#{isAssure}


			AND (CUST_REALNAME like CONCAT('%',#{searchOrderName},'%') or CUST_CELLPHONE like  CONCAT('%',#{searchOrderName},'%' ) or CONTRACT_ID like  CONCAT('%',#{searchOrderName},'%' ))


			AND (COMPANY_ID like CONCAT('%',#{searchCompanyName},'%') or COMPANY_NAME like  CONCAT('%',#{searchCompanyName},'%'))


   and CREATE_TIME >=DATE_FORMAT(#{beginDate},'%Y-%m-%d')


   and CREATE_TIME <= DATE_FORMAT(DATE_SUB(#{endDate},INTERVAL -1 DAY),'%Y-%m-%d')

 order by create_time desc

select 

from order_baseinfo
where 1=1

	and CUST_CELLPHONE=#{custCellphone}


	and ORDER_STATUS=#{orderStatus}

select 

from order_baseinfo
where  CUST_CELLPHONE=#{custCellphone} and ORDER_STATUS  <  #{orderStatus}
